(new WOW).init();const header=document.querySelector("#header"),headerTop=header.offsetTop;function stickyNavigation(){window.scrollY>=headerTop?header.classList.add("header__sticky--active"):header.classList.remove("header__sticky--active")}window.addEventListener("scroll",stickyNavigation);const anchors=document.querySelectorAll('a[href*="#"]');for(let r of anchors)r.addEventListener("click",function(e){e.preventDefault();e=r.getAttribute("href").substr(1);document.getElementById(e).scrollIntoView({behavior:"smooth",block:"start"})});let burgerMenu=document.getElementById("burgerMenu"),burgerLinks=document.querySelector(".header__nav--links-mob"),burgerNotOpen=document.querySelector(".burger__not-open"),burgerOpen=document.querySelector(".burger__open"),burgerLink=document.querySelectorAll("#burgerLink");function closeBurger(){burgerLinks.classList.toggle("menu-grid"),burgerOpen.classList.toggle("none"),burgerNotOpen.classList.toggle("none")}burgerMenu.addEventListener("click",()=>{closeBurger()}),burgerLink.forEach(e=>e.addEventListener("click",closeBurger));